mapper.insert:
INSERT INTO BOOK (
    BOOK_TITLE
    , AUTHOR
    , PUBLISHER
    , BOOK_PRICE
    , BOOK_STOCK
    , BOOK_INTRO
) VALUES(
    #{bookTitle}
    , #{author}
    , #{publisher}
    , #{bookPrice}
    , #{bookStock}
    , #{bookIntro}
)

mapper.update:
UPDATE BOOK
SET
    BOOK_TITLE = #{bookTitle}
    , AUTHOR = #{author}
    , PUBLISHER = #{publisher}
    , BOOK_PRICE = #{bookPrice}
    , BOOK_STOCK = #{bookStock}
    , BOOK_INTRO = #{bookIntro}
WHERE BOOK_NUM = #{bookNum}
